import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';

import { useState, useEffect, Fragment } from 'react';
import Header from './Header';
import Overview from './Overview';
import Files from './Files';
import Calendar from './Calendar';
import Login from './Login';

const PageSwitcher = ({index}) => {
  let component = null;
  switch(index) {
    case 0:
      component = <Login />
      break;
    case 1:
      component = <Overview />;
      break;
    case 2:
      component = <Files />;
      break;
    case 3:
      component = <Calendar />;
      break;
    default:
      component = <Login/>;
  }

  return component;
}
const App = () => {
  const [pageSwitcher, setSwitcher] = useState(1);
  const [currentPageIndex, setCurrentPageIndex] = useState(1);

  useEffect(()=> {
    if (currentPageIndex != pageSwitcher) {
      setCurrentPageIndex(pageSwitcher)
    }
  }, [pageSwitcher]);

  return(
    <div>
      {
        currentPageIndex != 0 && <Header setSwitcher={setSwitcher}/>
      }
      <PageSwitcher index={currentPageIndex}/>
    </div>
  )
}

export default App;
