import { Fragment, useCallback, useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Input from '@mui/material/Input';
import FormControl from '@mui/material/FormControl';
import FormHelperText from '@mui/material/FormHelperText';
import SearchIcon from '@mui/icons-material/Search';
import Box from '@mui/material/Box';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import Button from '@mui/material/Button';

import Fab from '@mui/material/Fab';
import Zoom from '@mui/material/Zoom';


import { styled } from '@mui/material/styles';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';

import ButtonGroup from '@mui/material/ButtonGroup';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import AssessmentIcon from '@mui/icons-material/Assessment';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import SummarizeIcon from '@mui/icons-material/Summarize';

import { KeyboardArrowUp } from "@mui/icons-material";
import useScrollTrigger from "@mui/material/useScrollTrigger";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
    '& .MuiDialogContent-root': {
      padding: theme.spacing(2),
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));

  export interface DialogTitleProps {
    id: string;
    children?: React.ReactNode;
    onClose: () => void;
}

function BootstrapDialogTitle(props: DialogTitleProps) {
    const { children, onClose, ...other } = props;

    return (
      <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
        {children}
        {onClose ? (
          <IconButton
            aria-label="close"
            onClick={onClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <CloseIcon />
          </IconButton>
        ) : null}
      </DialogTitle>
    );
  }


const Overview = () => {
    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };

    const trigger = useScrollTrigger({
        target: document.body,
        disableHysteresis: true,
        threshold: 100,
    });

    const scrollToTop = useCallback(() => {
        document.body.scrollTo({ top: 0, behavior: "smooth" });
    }, []); 

    return (
        <Fragment>
            <CssBaseline />
            <Container maxWidth="lg">
                <Grid container>
                    <Grid item xs={7} />
                    <Grid item xs={5}>
                        <FormControl variant="standard" sx={{ mb: 1, mt: 6, width: '100%', minWidth: '5ch' }}>
                            <Input
                                id="standard-adornment-search"
                                endAdornment={<SearchIcon position="end"></SearchIcon>}
                                aria-describedby="standard-search-helper-text"
                                inputProps={{
                                'aria-label': 'Search',
                                }}
                            />
                            <FormHelperText id="standard-search-helper-text">Search Tasks / Outputs / Date</FormHelperText>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Typography variant="h4" style={{fontWeight: 500}}>List of tasks</Typography>
                    </Grid>
                    <Grid item xs={12}>
                        <ButtonGroup variant="text" aria-label="text button group">
                            <Button startIcon={<AddCircleOutlineIcon />} onClick={handleClickOpen}>Create a task</Button>
                            <Button startIcon={<AssessmentIcon />}>Generate latest RACI</Button>
                            <Button startIcon={<SummarizeIcon />}>Generate Monthly Accomplishment Report</Button>
                        </ButtonGroup>
                    </Grid>
                    <Grid item xs={12}>
                        <Box sx={{ p: 2, mt: 2, mb: 2, 
                            boxShadow: 1, 
                            borderRadius: 2,
                            backgroundColor: 'grey'}}>
                            {
                            Array.from({length: 10}, () => Math.floor(Math.random() * 40)).map(()=> (
                                    <Accordion>
                                        <AccordionSummary
                                        expandIcon={<ExpandMoreIcon />}
                                        aria-controls="panel1a-content"
                                        id="panel1a-header"
                                        >
                                            <Grid container>
                                                <Grid item xs={7}>
                                                    <Typography style={{fontWeight: 500}}>2.2.1 EWS-L Monitoring Tools Maintenance</Typography>
                                                    <Typography variant="overline">Update python version</Typography>
                                                </Grid>
                                                <Grid item xs={5}>
                                                    <Grid container sx={{textAlign: 'right', pt: 1}} justifyContent="flex-end">
                                                        <Grid item xs={4}>
                                                            <Button startIcon={<PlayCircleOutlineIcon/>}>Start timer</Button>
                                                        </Grid>
                                                        <Grid item xs={4}>
                                                            <Button startIcon={<CheckCircleOutlineIcon/>} color="success">Mark as done</Button>
                                                        </Grid>
                                                    </Grid>
                                                </Grid>
                                            </Grid>
                                        </AccordionSummary>
                                        <AccordionDetails>
                                        <Typography>
                                            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse
                                            malesuada lacus ex, sit amet blandit leo lobortis eget.
                                        </Typography>
                                        </AccordionDetails>
                                    </Accordion>
                            )) 
                            }
                        </Box>
                    </Grid>
                </Grid>
            </Container>
            <Zoom in={trigger}>
                <Box
                role="presentation"
                sx={{
                    position: "fixed",
                    bottom: 32,
                    right: 32,
                    zIndex: 1,
                }}
                >
                <Fab
                    onClick={scrollToTop}
                    color="primary"
                    size="small"
                    aria-label="scroll back to top"
                >
                    <KeyboardArrowUp />
                </Fab>
                </Box>
            </Zoom>
            <BootstrapDialog
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
            >
                <BootstrapDialogTitle id="customized-dialog-title" onClose={handleClose}>
                    Create a task
                </BootstrapDialogTitle>
                <DialogContent dividers>
                    <TextField
                        id="filled-helperText"
                        label="Helper text"
                        defaultValue="Default Value"
                        helperText="Some important text"
                        variant="filled"
                    />
                </DialogContent>
                <DialogActions>
                <Button autoFocus color="error" onClick={() => {
                    console.log("RESET")
                }}>
                    Reset form
                </Button>
                <Button autoFocus onClick={handleClose}>
                    Save changes
                </Button>
                </DialogActions>
            </BootstrapDialog>
        </Fragment>
    )
}

export default Overview;
