import { Fragment } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Input from '@mui/material/Input';
import FormControl from '@mui/material/FormControl';
import FormHelperText from '@mui/material/FormHelperText';
import InputAdornment from '@mui/material/InputAdornment';
import SearchIcon from '@mui/icons-material/Search';
import Box from '@mui/material/Box';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import NoteAddIcon from '@mui/icons-material/NoteAdd';

const Overview = () => {
    return (
        <Fragment>
            <CssBaseline />
            <Container maxWidth="lg">
                <div style={{height: '80%'}}>
                    <Grid container>
                        <Grid item xs={7} />
                        <Grid item xs={5}>
                            <FormControl variant="standard" sx={{ mb: 1, mt: 6, width: '100%', minWidth: '5ch' }}>
                                <Input
                                    id="standard-adornment-search"
                                    endAdornment={<SearchIcon position="end"></SearchIcon>}
                                    aria-describedby="standard-search-helper-text"
                                    inputProps={{
                                    'aria-label': 'Search',
                                    }}
                                />
                                <FormHelperText id="standard-search-helper-text">Search Tasks / Outputs / Date</FormHelperText>
                            </FormControl>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography variant="h4" style={{fontWeight: 500}}>List of tasks</Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <ButtonGroup variant="text" aria-label="text button group">
                                <Button startIcon={<AddCircleOutlineIcon />}>Create a task</Button>
                                <Button startIcon={<NoteAddIcon />}>Generate latest RACI</Button>
                                {/* <Button>Three</Button> */}
                            </ButtonGroup>
                        </Grid>
                        <Grid item xs={12}>
                            <Box sx={{ p: 2, mt: 2, mb: 2, 
                                boxShadow: 1, 
                                borderRadius: 2,
                                backgroundColor: 'grey',
                                height: '75%',
                                overflow: "hidden",
                                overflowY: "scroll"}}>
                                {
                                Array.from({length: 10}, () => Math.floor(Math.random() * 40)).map(()=> (
                                        <Accordion>
                                            <AccordionSummary
                                            expandIcon={<ExpandMoreIcon />}
                                            aria-controls="panel1a-content"
                                            id="panel1a-header"
                                            >
                                                <div>
                                                    <Typography style={{fontWeight: 500}}>2.2.1 EWS-L Monitoring Tools Maintenance</Typography>
                                                    <Typography variant="overline">Update python version</Typography>
                                                </div>
                                            </AccordionSummary>
                                            <AccordionDetails>
                                            <Typography>
                                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse
                                                malesuada lacus ex, sit amet blandit leo lobortis eget.
                                            </Typography>
                                            </AccordionDetails>
                                        </Accordion>
                                )) 
                                }
                            </Box>
                        </Grid>
                    </Grid>
                </div>
            </Container>
        </Fragment>
    )
}

export default Overview;
